import java.util.Scanner;

public class IncrementoDecremento {


	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a = 2;
		System.out.println("el resulado es" + a);
		int b = 4 + a;
		System.out.println("el resulado es" + b);
		int resultado01 = a + b;
		System.out.println("el resulado es" + resultado01);
		
		int resultado1 = a - b;
		System.out.println("el resulado es" + resultado1);
		
		int resultado2 = a * b;
		System.out.println("el resulado es" + resultado2);
		int resultado3 = a / b;
		System.out.println("el resulado es" + resultado3);
		
		int resultado4 = a % b;
		System.out.println("el resulado es" + resultado4);
		
		
		
		
		
		
		
		
		
		

	}

}
