
public class OperacionesVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int sumar = ((7 + 2) *5);
		
		System.out.println("el resultado de la operacion es:" + sumar);
		
		sumar += 1;
		
		System.out.println("el resultado de la operacion es:" + sumar);
	
		
		int resto = 235%7;
		
		System.out.println("el resto de la operacion es:" + resto);
		
		

	}

}
